---
title: Search
sitemap: false
---

pspan id="search-process"Loading/span results span id="search-query-container" style="display: none;"for "strong id="search-query"/strong"/span/p
ul id="search-results"/ul

script
	window.data = {
		{% for post in site.posts %}
			{% if post.title %}
				{% unless post.excludedinsearch %}
					{% if added %},{% endif %}
					{% assign added = false %}
					"{{ post.url | slugify }}": {
						"id": "{{ post.url | slugify }}",
						"title": "{{ post.title | xml_escape }}",
						"categories": "{{ post.categories | join: ", " | xml_escape }}",
						"url": " {{ post.url | xml_escape }}",
						"content": {{ post.content | striphtml | replaceregex: "[\s/\n]+"," " | strip | jsonify }}
					}
					{% assign added = true %}
				{% endunless %}
			{% endif %}
		{% endfor %}
	};
/script
script src="{{ site.baseurl }}/js/lunr.min.js"/script
script src="{{ site.baseurl }}/js/search.js"/script
